# The method to be tested
def ip_address_helper():
    try:
        host_name = socket.gethostname()
        host_ip = socket.gethostbyname(host_name)
        return host_ip
    except:
        return "IP Error!"

# Test cases
class TestIpAddressHelper(unittest.TestCase):

    # Test case for successful IP retrieval
    def test_ip_address_helper_success(self):
        ip = ip_address_helper()
